const campaignDetails = getMilestoneCampaign.data?.milestone_campaign

function googleTimeToDate(seconds, nanos) {
    // Convert seconds to milliseconds and add nanoseconds converted to milliseconds
    const milliseconds = (seconds * 1000) + (nanos / 1000000);
    return new Date(milliseconds);
}

function convertDurationToDays(duration) {
    const seconds = parseInt(duration.seconds, 10);
    const nanos = duration.nanos;

    const totalSeconds = seconds + nanos / 1000000000;
    const days = totalSeconds / (24 * 3600);

    return days;
}

