const milestoneRewardObj = disableMilestoneReward.data.milestone_reward;

if (milestoneRewardObj && milestoneRewardObj.reward_id) {
 utils.showNotification({
    title: "SUCCESS",
    description: `Reward disabled successfully. Reward ID: ${milestoneRewardObj.reward_id}`,
    notificationType: "success",
    duration: 5,
  });
} else {
  utils.showNotification({
    title: "ERROR",
    description: "Something went wrong while disbaling the reward",
    notificationType: "Error",
    duration: 5,
  });
}
