// Function to generate UUID
function generateUUID() {
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function (c) {
    var r = (Math.random() * 16) | 0,
      v = c === "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

// Function to get current time in seconds and nanoseconds
function getCurrentTime() {
  const now = new Date();
  const seconds = Math.floor(now.getTime() / 1000);
  const nanos = (now.getTime() % 1000) * 1e6; // convert remaining milliseconds to nanoseconds
  return {
    seconds: seconds,
    nanos: nanos,
  };
}

//function to convert days into google duration
function daysToDuration(days) {
  const SECONDS_IN_A_DAY = 86400;

  // Convert days to total seconds
  let totalSeconds = days * SECONDS_IN_A_DAY;

  return {
    seconds: totalSeconds,
    nanos: 0,
  };
}

//function to convert date into google timestamp
function convertToTimestamp(dateString) {
  // Parse the date string
  const date = new Date(dateString + "T00:00:00Z");

  // Compute seconds since Unix epoch
  const seconds = Math.floor(date.getTime() / 1000);

  return {
    seconds: seconds,
    nanos: 0,
  };
}

function stringToUUID(str) {
  function s4(hash) {
    return Math.floor((1 + hash) * 0x10000)
      .toString(16)
      .substring(1);
  }

  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash);
  }

  // Split hash into parts and use them to format UUID
  return (
    s4(hash & 0xffff) +
    s4((hash >>> 16) & 0xffff) +
    "-" +
    s4(hash & 0xffff) +
    "-" +
    s4((hash >>> 16) & 0xffff) +
    "-" +
    s4(hash & 0xffff) +
    "-" +
    s4(hash & 0xffff) +
    s4((hash >>> 16) & 0xffff)
  );
}

