const milestoneRewardObj = getMilestoneReward.data.milestone_reward;

if (milestoneRewardObj && milestoneRewardObj.reward_id) {
 utils.showNotification({
    title: "SUCCESS",
    description: `Reward fetched successfully. Reward ID: ${milestoneRewardObj.reward_id}`,
    notificationType: "success",
    duration: 5,
  });
  editFlowRewardInitialiser.trigger()
} else {
  utils.showNotification({
    title: "ERROR",
    description: `No reward found with the given reward id: ${milestoneRewardObj.reward_id}`,
    notificationType: "Error",
    duration: 5,
  });
}
