const milestoneCampaignObj = getMilestoneCampaign.data.milestone_campaign;

if (milestoneCampaignObj && milestoneCampaignObj.campaign_id) {
 utils.showNotification({
    title: "SUCCESS",
    description: `Campaign fetched successfully. Campaign ID: ${milestoneCampaignObj.campaign_id}`,
    notificationType: "success",
    duration: 5,
  });
  editFlowCampaignInitialiser.trigger()
} else {
  utils.showNotification({
    title: "ERROR",
    description: `No campaign found with the given campaign id: ${milestoneCampaignObj.campaign_id}`,
    notificationType: "Error",
    duration: 5,
  });
}
